const Carousel = ({ books, onCheckIn }) => {
  const scrollContainerRef = React.useRef();

  const scrollLeft = () => {
    if (scrollContainerRef.current) {
      scrollContainerRef.current.scrollBy({
        left: -300, // Adjust based on item size
        behavior: 'smooth',
      });
    }
  };

  const scrollRight = () => {
    if (scrollContainerRef.current) {
      scrollContainerRef.current.scrollBy({
        left: 300, // Adjust based on item size
        behavior: 'smooth',
      });
    }
  };

  return (
    <div className="carousel-container">
      <button className="carousel-button left" onClick={scrollLeft}>
        ◀
      </button>
      <ul className="book-list carousel" ref={scrollContainerRef}>
        {books.map((book) => (
          <li key={book._id} className="book-item">
            <div className="book-details">
              <h2>{book.title}</h2>
              <p>Author: {book.author}</p>
              <p>Description: {book.description}</p>
              <img src={book.image} alt={book.title} className="book-image" />
              <p>Status: {book.status}</p>
              {book.status === 'checked out' && (
                <p>Checked out by: {book.checkedOutBy}</p>
              )}
            </div>
            {book.status === 'checked out' && (
              <button className="check-in" onClick={() => onCheckIn(book._id)}>
                Check In
              </button>
            )}
          </li>
        ))}
      </ul>
      <button className="carousel-button right" onClick={scrollRight}>
        ▶
      </button>
    </div>
  );
};

