// src/App.js
import React, { useState, useEffect } from 'react';
import { getAvailableBooks, getCheckedOutBooks, checkOutBook, checkInBook } from './api';

function App() {
  const [availableBooks, setAvailableBooks] = useState([]);
  const [checkedOutBooks, setCheckedOutBooks] = useState([]);

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    const available = await getAvailableBooks();
    const checkedOut = await getCheckedOutBooks();
    setAvailableBooks(available.data);
    setCheckedOutBooks(checkedOut.data);
  };

  const handleCheckOut = async (id) => {
    await checkOutBook(id, { checkedOutBy: 'User', dueDate: new Date() });
    fetchBooks();
  };

  const handleCheckIn = async (id) => {
    await checkInBook(id);
    fetchBooks();
  };

  return (
    <div>
      <h1>Library Tracker</h1>
      <h2>Available Books</h2>
      <ul>
        {availableBooks.map(book => (
          <li key={book._id}>
            {book.title} by {book.author}
            <button onClick={() => handleCheckOut(book._id)}>Check Out</button>
          </li>
        ))}
      </ul>
      <h2>Checked Out Books</h2>
      <ul>
        {checkedOutBooks.map(book => (
          <li key={book._id}>
            {book.title} by {book.author} (Checked out by: {book.checkedOutBy})
            <button onClick={() => handleCheckIn(book._id)}>Check In</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
