// FILE: App.js
import React, { useEffect, useState } from 'react';
import { fetchBooks } from './db';

function App() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    async function getBooks() {
      const books = await fetchBooks();
      setBooks(books);
    }
    getBooks();
  }, []);

  return (
    <div>
      <h1>Library Books</h1>
      <ul>
        {books.map((book) => (
          <li key={book._id}>
            {book.title} by {book.author} (Checked out by: {book.checkedOutBy})
            <button onClick={() => handleCheckIn(book._id)}>Check In</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
