import React from "react";
import Slider from "react-slick";

const BookSlider = ({ title, books, onCheckIn }) => {
  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 700,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 1024,
        settings: { slidesToShow: 2 },
      },
      {
        breakpoint: 600,
        settings: { slidesToShow: 1 },
      },
    ],
  };

  return (
    <div className="book-section">
      <h2>{title}</h2>
      <Slider {...sliderSettings}>
        {books.map((book) => (
          <div key={book._id} className="book-item">
            <img
              src={book.image || "/placeholder.jpg"} // Show placeholder if no image
              alt={book.title}
              className="book-image"
            />
            <div className="book-details">
              <h2>{book.title}</h2>
              <p>Author: {book.author}</p>
              <p>Description: {book.description}</p>
              <p>Status: {book.status}</p>
              {book.checkedOutBy && <p>Checked out by: {book.checkedOutBy}</p>}
            </div>
            {onCheckIn && book.status === "checked out" && (
              <button className="check-in" onClick={() => onCheckIn(book._id)}>
                Check In
              </button>
            )}
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default BookSlider;
