import React, { useEffect, useState } from "react";
import axios from "axios";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import BookSlider from "./BookSlider"; // Import the BookSlider component
import CheckOutPage from "./CheckOutPage";
import "./App.css";

function App() {
  const [allBooks, setAllBooks] = useState([]);
  const [availableBooks, setAvailableBooks] = useState([]);
  const [unavailableBooks, setUnavailableBooks] = useState([]);

  const fetchBooks = async () => {
    try {
      const allBooksResponse = await axios.get("http://localhost:5000/books");
      const availableBooksResponse = await axios.get(
        "http://localhost:5000/books/available"
      );
      const unavailableBooksResponse = await axios.get(
        "http://localhost:5000/books/checked-out"
      );
      setAllBooks(allBooksResponse.data);
      setAvailableBooks(availableBooksResponse.data);
      setUnavailableBooks(unavailableBooksResponse.data);
    } catch (error) {
      console.error("Error fetching books:", error);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  const handleCheckIn = async (id) => {
    try {
      await axios.post(`http://localhost:5000/books/check-in/${id}`);
      fetchBooks();
    } catch (error) {
      console.error("Error checking in book:", error);
    }
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            <div className="App">
              <header className="App-header">
                <h1>Library</h1>
                <Link to="/checkout">
                  <button className="checkout-button">Check Out</button>
                </Link>
              </header>
              <div className="book-sections">
                <BookSlider title="All Books" books={allBooks} />
                <BookSlider title="Available Books" books={availableBooks} />
                <BookSlider
                  title="Unavailable Books"
                  books={unavailableBooks}
                  onCheckIn={handleCheckIn}
                />
              </div>
            </div>
          }
        />
        <Route
          path="/checkout/:bookId"
          element={<CheckOutPage />}
        />
        <Route
          path="/checkout"
          element={<CheckOutPage
            availableBooks={availableBooks}
            fetchBooks={fetchBooks}
          />}
        />
      </Routes>
    </Router>
  );
}

export default App;
