// FILE: App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    async function getBooks() {
      try {
        const response = await axios.get('http://localhost:5000/books/available');
        setBooks(response.data);
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    }
    getBooks();
  }, []);

  const handleCheckIn = async (id) => {
    try {
      await axios.post(`http://localhost:5000/books/check-in/${id}`);
      const response = await axios.get('http://localhost:5000/books/available');
      setBooks(response.data);
    } catch (error) {
      console.error('Error checking in book:', error);
    }
  };

  return (
    <div className="App">
      <h1>Library Books</h1>
      <ul className="book-list">
        {books.map((book) => (
          <li key={book._id} className="book-item">
            <div className="book-details">
              <h2>{book.title}</h2>
              <p>Author: {book.author}</p>
              <p>Publisher: {book.publisher}</p>
              <p>ISBN: {book.isbn}</p>
              <p>Status: {book.status}</p>
              {book.status === 'checked out' && <p>Checked out by: {book.checkedOutBy}</p>}
              {book.status === 'checked out' && <p>Due Date: {new Date(book.dueDate).toLocaleDateString()}</p>}
            </div>
            {book.status === 'checked out' && (
              <button onClick={() => handleCheckIn(book._id)}>Check In</button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
