// FILE: App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [allBooks, setAllBooks] = useState([]);
  const [availableBooks, setAvailableBooks] = useState([]);
  const [unavailableBooks, setUnavailableBooks] = useState([]);
  const [selectedBook, setSelectedBook] = useState(null);
  const [checkedOutBy, setCheckedOutBy] = useState('');

  useEffect(() => {
    async function getAllBooks() {
      try {
        const response = await axios.get('http://localhost:5000/books');
        setAllBooks(response.data);
      } catch (error) {
        console.error('Error fetching all books:', error);
      }
    }

    async function getAvailableBooks() {
      try {
        const response = await axios.get('http://localhost:5000/books/available');
        setAvailableBooks(response.data);
      } catch (error) {
        console.error('Error fetching available books:', error);
      }
    }

    async function getUnavailableBooks() {
      try {
        const response = await axios.get('http://localhost:5000/books/checked-out');
        setUnavailableBooks(response.data);
      } catch (error) {
        console.error('Error fetching unavailable books:', error);
      }
    }
    getAllBooks();
    getAvailableBooks();
    getUnavailableBooks();
  }, []);

  const handleCheckIn = async (id) => {
    try {
      const response = await axios.post(`http://localhost:5000/books/check-in/${id}`);
      const updatedBook = response.data; // The updated book object returned by the backend

      // Update state
      setAvailableBooks([...availableBooks, updatedBook]);
      setUnavailableBooks(unavailableBooks.filter((book) => book._id !== id));
      setAllBooks(allBooks.map((book) => (book._id === id ? updatedBook : book)));
    } catch (error) {
      console.error('Error checking in book:', error);
    }
  };


  const handleCheckOut = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`http://localhost:5000/books/check-out/${selectedBook}`, { checkedOutBy });
      const allBooksResponse = await axios.get('http://localhost:5000/books');
      const availableBooksResponse = await axios.get('http://localhost:5000/books/available');
      const unavailableBooksResponse = await axios.get('http://localhost:5000/books/checked-out');
      setAllBooks(allBooksResponse.data);
      setAvailableBooks(availableBooksResponse.data);
      setUnavailableBooks(unavailableBooksResponse.data);
      setSelectedBook(null);
      setCheckedOutBy('');
    } catch (error) {
      console.error('Error checking out book:', error);
    }

  };

  return (
    <div className="App">
      <h1>Library Books</h1>
      <div className="book-sections">
        <div className="book-section">
          <h2>All Books</h2>
          <ul className="book-list">
            {allBooks.map((book) => (
              <li key={book._id} className="book-item">
                <div className="book-details">
                  <h2>{book.title}</h2>
                  <p>Author: {book.author}</p>
                  <p>Publisher: {book.publisher}</p>
                  <p>ISBN: {book.isbn}</p>
                  <p>Status: {book.status}</p>
                  {book.status === 'checked out' && <p>Checked out by: {book.checkedOutBy}</p>}
                </div>
                {book.status === 'checked out' && (
                  <button onClick={() => handleCheckIn(book._id)}>Check In</button>
                )}
              </li>
            ))}
          </ul>
        </div>
        <div className="book-section">
          <h2>Available Books</h2>
          <ul className="book-list">
            {availableBooks.map((book) => (
              <li key={book._id} className="book-item">
                <div className="book-details">
                  <h2>{book.title}</h2>
                  <p>Author: {book.author}</p>
                  <p>Publisher: {book.publisher}</p>
                  <p>ISBN: {book.isbn}</p>
                  <p>Status: {book.status}</p>
                </div>
              </li>
            ))}
          </ul>
        </div>
        <div className="book-section">
          <h2>Unavailable Books</h2>
          <ul className="book-list">
            {unavailableBooks.map((book) => (
              <li key={book._id} className="book-item">
                <div className="book-details">
                  <h2>{book.title}</h2>
                  <p>Author: {book.author}</p>
                  <p>Publisher: {book.publisher}</p>
                  <p>ISBN: {book.isbn}</p>
                  <p>Status: {book.status}</p>
                  <p>Checked out by: {book.checkedOutBy}</p>
                </div>
                <button onClick={() => handleCheckIn(book._id)}>Check In</button>
              </li>
            ))}
          </ul>
        </div>
      </div>
      <h2>Check Out a Book</h2>
      <form onSubmit={handleCheckOut}>
        <label>
          Select Book:
          <select value={selectedBook} onChange={(e) => setSelectedBook(e.target.value)}>
            <option value="">Select a book</option>
            {availableBooks.map((book) => (
              <option key={book._id} value={book._id}>
                {book.title}
              </option>
            ))}
          </select>
        </label>
        <label>
          Checked Out By:
          <input type="text" value={checkedOutBy} onChange={(e) => setCheckedOutBy(e.target.value)} required />
        </label>
        <button type="submit">Check Out</button>
      </form>
    </div>
  );
}

export default App;
